CREATE OR REPLACE FUNCTION david.ft_fec_inicio_programa ( v_Id_Programa_Estudio integer,v_ID_SEMESTRE integer ) RETURNS timestamp(0) AS $body$
DECLARE


	p_FECHA	  timestamp(0);
		

BEGIN

  SELECT MIN(Amd.Fecha_Inicio) INTO STRICT p_FECHA
  FROM ACAD_SEMESTRE_PROGRAMA ASP INNER JOIN Acad_Modulo_Detalle AMD ON Amd.Id_Semestre_Programa=Asp.Id_Semestre_Programa
  WHERE Asp.Id_Programa_Estudio=v_Id_Programa_Estudio AND ID_SEMESTRE=v_ID_SEMESTRE
  GROUP BY Asp.Id_Programa_Estudio,ASP.ID_SEMESTRE;

  RETURN p_FECHA;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
