CREATE OR REPLACE FUNCTION david.ft_filial_alumno (p_id_persona integer) RETURNS varchar AS $body$
DECLARE

  p_Sede varchar(500);

BEGIN
    select Distinct Sede into STRICT p_Sede
    from (SELECT Aap.Sede from Vw_Alumno_Plan_Programa aap
          where aap.id_persona=p_id_persona and aap.estado=1
          order by Aap.Id_Nivel_Ensenanza,Aap.Nombre_Plan desc) alias0 LIMIT 1;
    return p_Sede;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
