CREATE OR REPLACE FUNCTION david.ft_generar_requisitos_beca (p_id_solicitud_mat_alum bigint) RETURNS bigint AS $body$
DECLARE
 resultado bigint;


BEGIN
	   SELECT count(*) INTO STRICT resultado FROM SOLICITUD_MAT_ALUM sma 
       INNER JOIN TIPO_REQUISITO_BECA trb ON sma.ID_TIPO_REQUISITO_BECA = trb.ID_TIPO_REQUISITO_BECA 
	   WHERE sma.ID_SOLICITUD_MAT_ALUM= p_id_solicitud_mat_alum 
	   AND trb.CODIGO  IN ('BEA','BIECA', 'CREAF', 'BDC', 'BEACA', 'BPDAC', 'BP', 'B18', 'BPECA', 'BEIEA', 'DE');

      RETURN(resultado);

    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
