CREATE OR REPLACE FUNCTION david.ft_horario_teoria (v_id_carga_curso integer) RETURNS varchar AS $body$
DECLARE

	p_horario	  varchar(2000);
		

BEGIN

	select horario into STRICT p_horario
  from (SELECT Accd.horario 
        from Acad_Carga_Curso_Docente accd inner join Tipo_Horario th on Accd.Id_Tipo_Horario=Th.Id_Tipo_Horario and Th.Codigo like 'T%'
        and accd.Id_Carga_Curso=v_id_carga_curso
        order by Th.Codigo) alias0 LIMIT 1;

return p_horario;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
