CREATE OR REPLACE FUNCTION david.ft_horas_mes_cd (p_id_persona bigint,p_id_semestre bigint,p_id_carga_curso_docente bigint,p_fecha_ini timestamp(0),p_fecha_fin timestamp(0)) RETURNS varchar AS $body$
DECLARE

  
    resultado varchar(4000);
    l_horario varchar(4000);
    l_total bigint;
    l_horario_tmp varchar(4000);
    l_j bigint;
    l_num_dia bigint;
    l_i bigint;
    l_n_resta bigint;
    l_fecha_ini timestamp(0);
    l_fecha timestamp(0);
    l_hora bigint:=0;
    l_horas bigint:=0;
    l_horas_totales bigint:=0;

    curDet CURSOR FOR
      SELECT AMD.ID_MODULO_DETALLE,AMD.ID_MODULO,ACCD.ID_TIPO_HORARIO,AMD.FECHA_INICIO,AMD.FECHA_FIN,length(ACCD.HORARIO) TOTAL,ACCD.HORARIO
      FROM vw_acad_carga_academica aca inner join acad_carga_curso_docente accd on accd.id_carga_curso=aca.id_carga_curso
                    inner join acad_modulo_detalle amd on Amd.Id_Modulo_Detalle=Accd.Id_Modulo_Detalle 
      WHERE accd.id_persona=p_id_persona and aca.id_semestre=p_id_semestre
      AND ACCD.ID_CARGA_CURSO_DOCENTE=p_id_carga_curso_docente;

  
BEGIN
  
    FOR curD in curDet LOOP
      l_horario:=curD.horario;
      l_total:=length(l_horario);
      l_horario_tmp := l_horario;
      l_j:=1;

      while l_j<=l_total loop
      
        l_horario_tmp := substr(l_horario_tmp,0,7);
        l_i:=1;
        l_num_dia:= (to_char(curD.fecha_inicio, 'd'))::numeric;
        l_n_resta:= l_num_dia - 1;
        l_fecha_ini:= curD.fecha_inicio - l_n_resta;
        l_fecha:=l_fecha_ini;

        while l_i<=7 LOOP
        
          l_hora:= substr(l_horario_tmp,l_i,1);
          if l_hora = '1' then

            if l_fecha>=p_fecha_ini and l_fecha <=p_fecha_fin then
              l_horas:=(l_hora)::numeric;
              --l_fecha:=l_fecha;
            END IF;

          END IF;

          l_horas_totales:=l_horas_totales+l_horas;
          
          l_fecha:= l_fecha + 1;
          l_i:=l_i + 1;

        END LOOP;

        l_j:=l_j + 7;
        l_horario_tmp := substr(l_horario,l_j,l_total);

      END LOOP;

    END LOOP;

    resultado:=l_horas||'----'||l_num_dia||'-*'||l_n_resta||'*------'||l_fecha_ini||'-'||l_fecha||'------'||p_fecha_ini||'-'||p_fecha_fin;

  return resultado;

  END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION david.ft_horas_mes_cd (p_id_persona bigint,p_id_semestre bigint,p_id_carga_curso_docente bigint,p_fecha_ini timestamp(0),p_fecha_fin timestamp(0)) FROM PUBLIC;

