CREATE OR REPLACE FUNCTION david.ft_hora_curso_n (HORARIO text,DIA text) RETURNS integer AS $body$
DECLARE


corte integer :=1;
hora integer :=0;
val varchar(200);
var varchar(200);


BEGIN

  FOR i IN 1..30 LOOP
  
      val := SUBSTR(HORARIO,corte ,corte+7);
      
      var := substr(val,DIA,1);

      IF var ='1' THEN
      hora:=hora+1;
      end if;

      corte:=corte+7;

  END LOOP;


RETURN hora;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
