CREATE OR REPLACE FUNCTION david.ft_id_programa_estudio ( v_codigo text) RETURNS integer AS $body$
DECLARE

	p_id	  integer;
		

BEGIN

	select id_programa_estudio into STRICT p_id
  from acad_programa_estudio
  where codigo like '%,'||v_codigo||'%';

return p_id;

END;
