CREATE OR REPLACE FUNCTION david.ft_licencia_sunedu (p_id_persona integer) RETURNS varchar AS $body$
DECLARE

  p_resultado varchar(500);

BEGIN
  select (case when anio>2013 then 'SI' else 'NO' end) into STRICT p_resultado
  from (SELECT min(substr(Aca.Semestre,0,4)) anio
        from vw_acad_carga_academica aca inner join acad_carga_curso_docente accd on accd.id_carga_curso=Aca.Id_Carga_Curso
        where Accd.Id_Persona=p_id_persona) alias3;
  return p_resultado;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
