CREATE OR REPLACE FUNCTION david.ft_listagg_id_curso_doc (p_id_carga_curso integer, p_id_persona integer) RETURNS varchar AS $body$
DECLARE

  res varchar(4000);

BEGIN

  SELECT   string_agg(  ID_CARGA_CURSO_DOCENTE ,',' ORDER BY ID_CARGA_CURSO_DOCENTE) into STRICT res

  FROM ( 
        SELECT DISTINCT accd.ID_CARGA_CURSO_DOCENTE 
        FROM david.VW_ACAD_CARGA_ACADEMICA vaca 
        INNER JOIN david.ACAD_CARGA_CURSO_DOCENTE accd ON accd.ID_CARGA_CURSO =vaca.ID_CARGA_CURSO 
        INNER JOIN david.ACAD_MODULO_DETALLE amd ON amd.ID_MODULO_DETALLE =accd.ID_MODULO_DETALLE 
        INNER JOIN david.VW_ACAD_CURSO_ALUMNO vaca2 ON vaca2.ID_CARGA_CURSO = vaca.ID_CARGA_CURSO 
        
        WHERE  
        vaca.id_carga_curso =p_id_carga_curso
        AND accd.id_persona = p_id_persona
  ) alias1;

  return res;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
