CREATE OR REPLACE FUNCTION david.ft_max_codigo_conv (v_num bigint,v_convocatoria text) RETURNS varchar AS $body$
DECLARE


  p_codigo varchar(20);

  p_codigo_semestre varchar(20);
  p_codigo_anio varchar(20);


BEGIN



      select coalesce((select max(codigo) from moises.persona_natural_alumno where /*codigo like substr(to_CHAR(sysdate,'YYYY-MM-DD'),0,4)||'%' OR*/
 codigo LIKE v_convocatoria::text||'%'),v_convocatoria||'0000')+v_num into STRICT p_codigo
;

      return p_codigo;

end;
