CREATE OR REPLACE FUNCTION david.ft_max_codigo_inter (v_num bigint) RETURNS varchar AS $body$
DECLARE


  p_codigo varchar(20);

  p_codigo_semestre varchar(20);
  p_codigo_anio varchar(20);


BEGIN



      select max('4'||substr(semestre,3,2)||substr(semestre,-1)) into STRICT p_codigo_semestre
      ---replace(substr(semestre,0,6),'-','')||'0000' into p_codigo_semestre
      from acad_semestre where estado_admision=1;


      select coalesce((select max(codigo) from moises.persona_natural_alumno where codigo LIKE p_codigo_semestre::text||'%'),p_codigo_semestre||'00000')+v_num into STRICT p_codigo
;

      return p_codigo;

end;
$body$
LANGUAGE PLPGSQL
