CREATE OR REPLACE FUNCTION david.ft_max_matricula_persona (v_id_persona integer) RETURNS varchar AS $body$
DECLARE

  p_semestre varchar(4000);

BEGIN
  select max(semestre) into STRICT p_semestre
  from Vw_Acad_Alumno_Contrato where Id_persona=v_id_persona and estado='1' and substr(semestre,0,4)>=2017
and Id_Tipo_Contrato in (1,2,4,5);
  return p_semestre;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
