CREATE OR REPLACE FUNCTION david.ft_max_semestre_alumno_eap ( V_ID_PERSONA bigint , V_ID_ESCUELA bigint ) RETURNS varchar AS $body$
DECLARE

p_semestre varchar(500);

BEGIN

  select max(Semestre) into STRICT p_semestre from Vw_Acad_Alumno_Contrato
  where id_persona=V_ID_PERSONA and id_escuela=V_ID_ESCUELA and estado='1' --and Id_Resp_Financiero is not null
;

  RETURN p_semestre;

END;
$body$
