CREATE OR REPLACE FUNCTION david.ft_max_semestre_alumno ( V_ID_PERSONA bigint , V_ID_PLAN_PROGRAMA bigint ) RETURNS varchar AS $body$
DECLARE

p_semestre varchar(500);

BEGIN
  SELECT MAX(ASM.SEMESTRE) SEMESTRE INTO STRICT p_semestre
  FROM ACAD_ALUMNO_CONTRATO AAC INNER JOIN ACAD_SEMESTRE_PROGRAMA ASP ON ASP.ID_SEMESTRE_PROGRAMA=AAC.ID_SEMESTRE_PROGRAMA
                              AND AAC.ID_PERSONA=V_ID_PERSONA
                              and AAC.ID_PLAN_PROGRAMA=V_ID_PLAN_PROGRAMA
                              AND AAC.ESTADO='1'
  INNER JOIN ACAD_SEMESTRE ASM ON ASM.ID_SEMESTRE=ASP.ID_SEMESTRE WHERE SUBSTR(ASM.SEMESTRE,-2) IN ('-1','-2');

  RETURN p_semestre;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
