CREATE OR REPLACE FUNCTION david.ft_merito_semestre (p_id_persona integer,p_id_programa_estudio integer,p_id_semestre integer,p_ciclo integer) RETURNS bigint AS $body$
DECLARE

  p_num bigint;

BEGIN
    
    select puesto into STRICT p_num
    from (SELECT row_number() OVER () AS puesto,a.id_persona,a.credito_ciclo
          from (
                SELECT aac.id_persona,Aac.Id_Plan_Programa,aac.ciclo,
                Ft_Ponderado_Ciclo(aac.id_persona,aac.id_semestre,aac.id_nivel_ensenanza) as credito_ciclo
                from Vw_Acad_Alumno_Contrato aac
                where aac.id_semestre=p_id_semestre and aac.estado='1' and Aac.Id_Modo_Contrato=1 
                and Aac.Id_Programa_Estudio=p_id_programa_estudio and aac.ciclo=p_ciclo
                order by credito_ciclo desc) a ) alias2 
    where id_persona=p_id_persona;

    return p_num;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
