CREATE OR REPLACE FUNCTION david.ft_modalidad_ingreso (p_id_persona integer,p_id_plan_programa integer) RETURNS varchar AS $body$
DECLARE

  p_modalidad varchar(2000);

BEGIN

  select max(Mi.Nombre) into STRICT p_modalidad
  from CALEB.Adm_Persona_Inscripcion pi inner join CALEB.Adm_Conv_Plan_Programa cpp on Cpp.Id_Conv_Plan_Programa=Pi.Id_Conv_Plan_Programa
  inner join vw_Plan_Programa app on app.Id_Plan_Programa=Cpp.Id_Plan_Programa
  inner join genesis.persona p on P.Id_Persona=Pi.Id_Persona
  inner join CALEB.Adm_Modalidad_Ingreso mi on Mi.Id_Modalidad_Ingreso=Pi.Id_Modalidad_Ingreso
  where P.Id_Persona_Lamb=45142 and App.Id_escuela=(SELECT p_id_persona from vw_Plan_Programa where id_plan_programa=p_id_plan_programa);

  return p_modalidad;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
