CREATE OR REPLACE FUNCTION david.ft_new_codigo_acad (v_id_persona bigint) RETURNS varchar AS $body$
DECLARE

  p_codigo varchar(20);

BEGIN
  SELECT SINTILDE(CONCAT(CONCAT(TO_CHAR(clock_timestamp(),'YYYY'),SUBSTR(UPPER(conv_char@dbl_noe(REPLACE(CONVERT(SUBSTR(UPPER(PATERNO),1,2),'US7ASCII','WE8ISO8859P1'),'?','X'))),1,2)),TO_CHAR(clock_timestamp(),'YYYYMMDDHH24MI')||LPAD(1,2,'0'))) INTO STRICT p_codigo
  FROM MOISES.PERSONA
  WHERE ID_PERSONA=v_id_persona;
  RETURN p_codigo;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
