CREATE OR REPLACE FUNCTION david.ft_nivel_riesgo_ant (v_ponderado bigint, v_desaprobados bigint, v_tipo text) RETURNS varchar AS $body$
DECLARE

  p_res	  varchar(500);		


BEGIN
    
    if (v_tipo='R') then
    
      select (case when round((v_ponderado)::numeric,0)<13 then 'MA' 
              when round((v_ponderado)::numeric,0)>=13 
                    and v_desaprobados>=1 then 'A' 
              when round((v_ponderado)::numeric,0)=13 
                    and v_desaprobados=0 then 'A' 
              when round((v_ponderado)::numeric,0)>=14 
                    and v_desaprobados>=1 and v_desaprobados<=2 then 'M' 
              when round((v_ponderado)::numeric,0)=14 
                    and v_desaprobados=0 then 'M' 
              when round((v_ponderado)::numeric,0)>=15 
                    and v_desaprobados=0 then 'B' else 'B' 
          end) into STRICT p_res
;

    elsif (v_tipo='C') then
    
      select (case when round((v_ponderado)::numeric,0)<13 then 'Muy Alto' 
              when round((v_ponderado)::numeric,0)>=13 
                    and v_desaprobados>=1 then 'Alto' 
              when round((v_ponderado)::numeric,0)=13 
                    and v_desaprobados=0 then 'Alto' 
              when round((v_ponderado)::numeric,0)>=14 
                    and v_desaprobados>=1 and v_desaprobados<=2 then 'Medio' 
              when round((v_ponderado)::numeric,0)=14 
                    and v_desaprobados=0 then 'Medio' 
              when round((v_ponderado)::numeric,0)>=15 
                    and v_desaprobados=0 then 'Bajo' else 'Bajo' 
          end) into STRICT p_res
;

    end if;

 return p_res;

END;
$body$
