CREATE OR REPLACE FUNCTION david.ft_nom_escuela_anterior (p_id_persona integer,p_id_escuela_act integer) RETURNS varchar AS $body$
DECLARE


p_escuela varchar(200);


BEGIN

  select Distinct Nombre_Certificado into STRICT p_escuela
  from (
        SELECT Ape.Nombre_Certificado from Vw_Alumno_Plan_Programa a inner join Vw_Acad_Programa_Estudio ape on Ape.Id_Programa_Estudio=A.Id_Programa_Estudio
        where id_persona=p_id_persona and a.id_escuela<>p_id_escuela_act and a.Id_Nivel_Ensenanza in (1,2)
        order by a.Fecha_Registro desc) alias1 LIMIT 1;

  return p_escuela;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
