CREATE OR REPLACE FUNCTION david.ft_num_atenciones_fecha_eap (v_id_semestre bigint,v_mes text , v_id_programa_estudio bigint) RETURNS bigint AS $body$
DECLARE


    num_estudiantes bigint;


BEGIN

    select count(id_adviser) into STRICT num_estudiantes
    from (SELECT Distinct id_adviser
          from adviser_sesion ads inner join adviser_tutorando adt on Adt.Id_Adviser_Tutorando=Ads.Id_Adviser_Tutorando
          inner join acad_Plan_Programa app on App.Id_Plan_Programa=Adt.Id_Plan_Programa
          inner join Acad_Programa_Estudio ape on Ape.Id_Programa_Estudio=App.Id_Programa_Estudio
          where ads.id_semestre=v_id_semestre
          and to_char(to_date(substr(ads.Fecha_Sesion,0,10),'yyyy-mm-dd hh:mi:ss'),'MM')=v_mes
          and Ape.Id_Programa_Estudio=v_id_programa_estudio ) alias4;

    return num_estudiantes;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
