CREATE OR REPLACE FUNCTION david.ft_num_derivaciones_ciclo_eapg (v_id_semestre bigint,v_id_adviser_derivacion bigint , v_id_programa_estudio bigint) RETURNS bigint AS $body$
DECLARE




    num_estudiantes bigint;




BEGIN



    select count(id_persona) into STRICT num_estudiantes

    from (SELECT Distinct adt.id_persona 

          from Adviser_Sesion_Derivacion asd 
          inner join Adviser_Sesion ads on Ads.Id_Adviser_Sesion=Asd.Id_Adviser_Sesion

          inner join adviser_tutorando adt on (adt.id_persona=ads.id_persona AND ads.id_semestre=adt.id_semestre)

          inner join acad_Plan_Programa app on App.Id_Plan_Programa=Adt.Id_Plan_Programa

          inner join Acad_Programa_Estudio ape on Ape.Id_Programa_Estudio=App.Id_Programa_Estudio 

          where Ads.Id_Semestre=v_id_semestre

          and Asd.Id_Adviser_Derivacion=v_id_adviser_derivacion

          and Ape.Id_Programa_Estudio=v_id_programa_estudio) alias2;



    return num_estudiantes;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
