CREATE OR REPLACE FUNCTION david.ft_observacion_adviser ( p_id_adviser_sesion text) RETURNS varchar AS $body$
DECLARE

    r_texto varchar(5000);

BEGIN

	
    SELECT substr(detalle,1,50) ||'...'  INTO STRICT r_texto FROM adviser_sesion_Acad sa
    INNER JOIN adviser_bloque_Det ab ON sa.ID_ADVISER_BLOQUE_DET =ab.ID_ADVISER_BLOQUE_DET
    WHERE ab.codigo='2020002'
    AND sa.id_adviser_sesion=p_id_adviser_sesion  LIMIT 1;

    

    RETURN r_texto;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
