CREATE OR REPLACE FUNCTION david.ft_periodo_ingreso (p_id_persona integer,p_id_plan_programa integer) RETURNS varchar AS $body$
DECLARE

  p_semestre varchar(20);

BEGIN

  select min(cv.nombre) into STRICT p_semestre   
  from CALEB.Adm_Persona_Inscripcion pi inner join CALEB.Adm_Conv_Plan_Programa cpp on Cpp.Id_Conv_Plan_Programa=Pi.Id_Conv_Plan_Programa
  inner join vw_Plan_Programa app on app.Id_Plan_Programa=Cpp.Id_Plan_Programa
  inner join genesis.persona p on P.Id_Persona=Pi.Id_Persona
  inner join CALEB.Adm_Convocatoria_Det cd on Cd.Id_Convocatoria_Det=Cpp.Id_Convocatoria_Det
  inner join CALEB.Adm_Convocatoria cv on Cv.Id_Convocatoria=Cd.Id_Convocatoria
  where P.Id_Persona_Lamb=p_id_persona and App.Id_escuela=(SELECT Id_escuela from vw_Plan_Programa where id_plan_programa=p_id_plan_programa);

  return p_semestre;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
