CREATE OR REPLACE FUNCTION david.ft_permiso_aviso ( ID_PERMISO_P bigint ) RETURNS varchar AS $body$
DECLARE

    RESULTADO varchar(100);

BEGIN
    SELECT (CASE
                WHEN (clock_timestamp() BETWEEN RP.FECHA_HINICIO AND (RP.FECHA_HFIN + (coalesce(RP.HORA_EXTENSION, 0) / 24) +
                                                            (coalesce(RP.MINUTO_EXTENSION, 0) / 1440)))
                    THEN (
                    CASE
                        WHEN clock_timestamp() >= ((RP.FECHA_HFIN + (coalesce(RP.HORA_EXTENSION, 0) / 24) +
                                          (coalesce(RP.MINUTO_EXTENSION, 0) / 1440)) - 15 / 1440)
                            THEN (
                            CASE
                                WHEN clock_timestamp() >= ((RP.FECHA_HFIN + (coalesce(RP.HORA_EXTENSION, 0) / 24) +
                                                  (coalesce(RP.MINUTO_EXTENSION, 0) / 1440)) - 5 / 1440)
                                    THEN 'Falta menos de 5 minutos para vencer'
                                ELSE 'Falta menos de 15 minutos para vencer' END
                            )

                        ELSE 'Esta a tiempo' END
                    )
                ELSE 'Permiso vencido' END)
    INTO STRICT RESULTADO
    FROM DAVID.RESIDENCIA_PERMISO RP
    WHERE RP.ID_PERMISO = ID_PERMISO_P;

    RETURN RESULTADO;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
