CREATE OR REPLACE FUNCTION david.ft_planp_alumno_id (P_ID_PERSONA integer) RETURNS bigint AS $body$
DECLARE

  P_ID_PLAN_PROGRAMA bigint;

BEGIN
    SELECT distinct ID_PLAN_PROGRAMA INTO STRICT P_ID_PLAN_PROGRAMA
    FROM DAVID.VW_ALUMNO_PLAN_PROGRAMA VPP
    WHERE  ID_PERSONA=P_ID_PERSONA
    AND ID_PLAN_PROGRAMA = (SELECT  ID_PLAN_PROGRAMA
                            FROM (SELECT ID_PLAN_PROGRAMA,ID_PERSONA FROM DAVID.VW_ALUMNO_PLAN_PROGRAMA WHERE ESTADO=1 ORDER BY ID_FACULTAD) alias0 
                            WHERE ID_PERSONA=VPP.ID_PERSONA  LIMIT 1);
    RETURN P_ID_PLAN_PROGRAMA;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
