CREATE OR REPLACE FUNCTION david.ft_ponderado_anio_plan (p_id_persona integer,p_id_plan_programa integer,p_anio integer) RETURNS bigint AS $body$
DECLARE

  p_plan bigint;
  p_promedio bigint;

BEGIN

  select Id_Plan into STRICT p_plan from Vw_Plan_Programa where id_plan_programa=p_id_plan_programa;

  select round((sum(promedio*credito)/CASE WHEN sum(credito)=0 THEN 1  ELSE sum(credito) END ),2) into STRICT p_promedio
  from (SELECT Distinct acp.id_curso_detalle,acp.Id_Plan_Curso,acp.ciclo,curso,Aca.Promedio,aca.id_tipo_condicion,Acp.Credito
        from acad_plan_programa app inner join Vw_Acad_Curso_Plan acp on Acp.Id_Plan=App.Id_Plan
        inner join acad_curso_alumno aca on aca.id_persona=p_id_persona
                  and aca.Id_Plan_Curso=CASE WHEN coalesce(acp.Id_Plan_Curso_Electivo::text, '') = '' THEN acp.Id_Plan_Curso  ELSE acp.Id_Plan_Curso_Electivo END 
                  and aca.estado='1' and aca.Id_Tipo_Condicion not in(3,7,15)
        where app.ID_PLAN_PROGRAMA=p_id_plan_programa and coalesce(Acp.Id_Plan_Curso_Electivo::text, '') = '' ) alias8
    where floor((ciclo+1)/2)=p_anio;

  return p_promedio;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
