CREATE OR REPLACE FUNCTION david.ft_ponderado_ciclo_escuela (p_id_persona integer,p_id_escuela integer,p_id_semestre integer) RETURNS bigint AS $body$
DECLARE


  p_promedio bigint;


BEGIN



  select coalesce((select round((sum(coalesce(aca.Promedio,0)*aca.Credito)/CASE WHEN sum(aca.Credito)=0 THEN 1  ELSE sum(aca.Credito) END )::numeric,2)
  from Vw_Acad_Curso_Alumno  aca 
  where aca.id_persona=p_id_persona
  and aca.id_semestre=p_id_semestre
  and aca.estado='1' and aca.id_tipo_condicion in (1,2,9,13,17)-- and aca.cerrado='S'
  and Aca.Id_escuela=p_id_escuela),0) into STRICT p_promedio
;



  return p_promedio;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
