CREATE OR REPLACE FUNCTION david.ft_ponderado_ciclo (p_id_persona integer,p_id_semestre integer,p_id_nivel_ensenanza integer) RETURNS bigint AS $body$
DECLARE


  p_promedio bigint;


BEGIN



  select round((sum(coalesce(aca.Promedio,0)*aca.Credito)/CASE WHEN sum(aca.Credito)=0 THEN 1  ELSE sum(aca.Credito) END )::numeric,2) into STRICT p_promedio

  from Vw_Acad_Curso_Alumno  aca inner join Acad_Programa_Estudio ape on Ape.Id_Programa_Estudio=Aca.Id_Programa_Estudio

  inner join acad_carga_curso acc on acc.id_carga_curso=aca.id_carga_curso

  where aca.id_persona=p_id_persona

  and aca.id_semestre=p_id_semestre

  and aca.estado='1' and id_tipo_condicion<>'3' and acc.cerrado='S'

  and Ape.Id_Nivel_Ensenanza=p_id_nivel_ensenanza;



  return p_promedio;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
