CREATE OR REPLACE FUNCTION david.ft_posgrado_persona (v_id_persona integer,v_semestre text) RETURNS varchar AS $body$
DECLARE

  p_escuela varchar(4000);

BEGIN
  select Nom_Escuela into STRICT p_escuela
  from (SELECT distinct 2 dato,CASE WHEN Vpp.Id_Tipo_Contrato=3 THEN 6  ELSE Vpp.Id_Tipo_Contrato END ,Vpp.Nom_Escuela 
        from moises.persona_natural_egresado pne inner join Vw_Plan_Programa vpp on Vpp.Id_Plan_Programa=Pne.Id_Plan_Programa
        where id_persona=v_id_persona and Vpp.Id_Tipo_Contrato in (4,5,3,7,17) 
        
union

        SELECT 1 dato,CASE WHEN Id_Tipo_Contrato=3 THEN 6  ELSE Id_Tipo_Contrato END ,Nombre_Escuela from Vw_Acad_Alumno_Contrato 
        where id_persona=v_id_persona and Id_Tipo_Contrato in (4,5,3,7,17) and semestre=v_semestre and estado='1' 
        order by 1,2) alias2 LIMIT 1;
  return p_escuela;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
