CREATE OR REPLACE FUNCTION david.ft_primera_matricula_alumnon ( V_ID_PERSONA bigint, V_ID_PLAN_PROGRAMA bigint,V_SEMESTRE text) RETURNS varchar AS $body$
DECLARE

p_escuela integer;
p_semestre varchar(500);

BEGIN

  SELECT ID_ESCUELA INTO STRICT p_escuela FROM VW_PLAN_PROGRAMA WHERE ID_PLAN_PROGRAMA=V_ID_PLAN_PROGRAMA;

  SELECT MIN(AAC.SEMESTRE) SEMESTRE INTO STRICT p_semestre
  FROM VW_ACAD_ALUMNO_CONTRATO AAC INNER JOIN VW_ACAD_PROGRAMA_ESTUDIO APE ON APE.ID_PROGRAMA_ESTUDIO=AAC.ID_PROGRAMA_ESTUDIO
  WHERE AAC.ID_MODO_CONTRATO=1 AND AAC.ESTADO='1' AND SUBSTR(AAC.SEMESTRE,-2) IN ('-1','-2')
  AND APE.ID_ESCUELA=p_escuela AND ID_PERSONA=V_ID_PERSONA AND AAC.SEMESTRE>=V_SEMESTRE;

  RETURN p_semestre;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
