CREATE OR REPLACE FUNCTION david.ft_profesor_teoria (v_id_carga_curso integer) RETURNS varchar AS $body$
DECLARE

	p_nombre	  varchar(2000);
		

BEGIN

	select p.paterno||' '||p.materno||' '||p.nombre into STRICT p_nombre
  from MOISES.Persona p inner join(SELECT Accd.Id_Persona 
                                    from Acad_Carga_Curso_Docente accd inner join Tipo_Horario th on Accd.Id_Tipo_Horario=Th.Id_Tipo_Horario and Th.Codigo like 'T%'
                                    and accd.Id_Carga_Curso=v_id_carga_curso
                                    order by Th.Codigo) d on P.Id_Persona=D.Id_Persona   LIMIT 1;

return p_nombre;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
