CREATE OR REPLACE FUNCTION david.ft_promedio_general_plan (p_id_persona integer,p_id_plan_programa integer) RETURNS bigint AS $body$
DECLARE

  p_plan bigint;
  p_area bigint;
  p_anio bigint;
  p_promedio bigint;

BEGIN

  select round((sum(coalesce(aca.Promedio,0))/CASE WHEN sum(acd.Credito)=0 THEN 1  ELSE sum(acd.Credito) END )::numeric,2) into STRICT p_promedio
  from Acad_Curso_Alumno aca inner join Acad_Plan_Curso apc on Apc.Id_Plan_Curso=aca.Id_Plan_Curso
  left join Acad_Plan_Curso apce on Apce.Id_Plan_Curso=Apc.Id_Plan_Curso_Electivo
  inner join acad_curso_detalle acd on Acd.Id_Curso_Detalle=coalesce(apce.id_curso_detalle,apc.id_curso_detalle)
  where aca.id_persona=p_id_persona
  and aca.Id_Plan_Programa=p_id_plan_programa
  and aca.estado='1' and id_tipo_condicion in ('1','2');

  return p_promedio;
 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
