CREATE OR REPLACE FUNCTION david.ft_resultado_aprendizaje ( p_id_carga_unidad bigint ) RETURNS varchar AS $body$
DECLARE

    r_descripcion varchar(4000);

BEGIN

SELECT
coalesce(accra.DESCRIPCION ,ra.DESCRIPCCION )  INTO STRICT r_descripcion
FROM ACAD_CARGA_UNIDAD_RA accra 
left JOIN RESULTADO_APRENDIZAJE ra ON ra.ID_RESULTADO_APRENDIZAJE = accra.ID_RESULTADO_APRENDIZAJE 
WHERE accra.ID_CARGA_UNIDAD  = p_id_carga_unidad  LIMIT 1;

    RETURN r_descripcion;
END;
$body$
LANGUAGE PLPGSQL
