CREATE OR REPLACE FUNCTION david.ft_semestre_post ( v_semestre text) RETURNS varchar AS $body$
DECLARE

	p_semestre	  varchar(800);
		

BEGIN

	select (case when substr(v_semestre,-1) ='2' then substr(v_semestre,0,4)+1||'-1'
          else substr(v_semestre,0,4)||'-2'
          end ) into STRICT p_semestre
;

return p_semestre;

END;
