CREATE OR REPLACE FUNCTION david.ft_tarea ( p_id_carga_unidad bigint ) RETURNS varchar AS $body$
DECLARE

    r_descripcion varchar(4000);

BEGIN

SELECT
acct.DESCRIPCION INTO STRICT r_descripcion
FROM 
ACAD_CARGA_CURSO_TAREA acct  
INNER JOIN ACAD_CARGA_UNIDAD acu ON acct.ID_CARGA_UNIDAD =acu.ID_CARGA_UNIDAD 
WHERE acct.ID_CARGA_UNIDAD  = p_id_carga_unidad  LIMIT 1;

    RETURN r_descripcion;
END;
