CREATE OR REPLACE FUNCTION david.ft_tercio_nom_curso (v_id_semestre bigint, v_id_persona bigint, v_id_sede bigint, v_nom_curso text) RETURNS bigint AS $body$
DECLARE

    p_puesto bigint;


BEGIN

    p_puesto:=0;

    select min(puesto) into STRICT p_puesto from (
    SELECT row_number() OVER () AS puesto,id_persona
    from (SELECT id_persona,Promedio
          from Vw_Acad_Curso_Alumno 
          where id_semestre=v_id_semestre and Nombre_Curso=v_nom_curso
          and id_sede=v_id_sede
          order by Promedio desc) alias1 ) alias2 
    where id_persona=v_id_persona and puesto Between 1 and 3;

    return p_puesto;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
