CREATE OR REPLACE FUNCTION david.ft_tiene_deuda (v_id_persona integer,v_id_semestre integer) RETURNS bigint AS $body$
DECLARE

  p_res bigint;
  p_monto bigint;
  p_sem varchar(50);

BEGIN

  select Semestre into STRICT p_sem
  from acad_semestre
  where Id_Semestre=v_id_semestre;

      select coalesce((select substr(deuda,4) monto
              from (select ft_deuda_alumno(v_id_persona,substr(p_sem,0,4)) deuda ) alias3
              where substr(deuda,0,2)='01' ),0) into STRICT p_monto
;

      if (p_monto>0) then
      
        p_res:=1;

      else
      
        p_res:=0;

      end if;

  return p_res;
      
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
