CREATE OR REPLACE FUNCTION david.ft_tiene_segundociclo (v_id_persona integer,v_id_plan_programa integer) RETURNS bigint AS $body$
DECLARE

  p_res bigint;
  p_ciclo bigint;

BEGIN

  select coalesce((select max(Ciclo)
              from vw_acad_alumno_contrato where id_persona=v_id_persona and Id_Modo_Contrato=1
              and Id_Plan_Programa=v_id_plan_programa and estado='1'),0) into STRICT p_ciclo 
;

      if (p_ciclo>=2) then
        p_res:=0;
      else
        p_res:=1;
      end if;

  return p_res;
      
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
