CREATE OR REPLACE FUNCTION david.ft_tipo_rub_eval (p_id_carga_curso integer,p_tipo text) RETURNS bigint AS $body$
DECLARE

  p_resultado bigint;


BEGIN

    if (p_tipo='T') then
    
      select count(*) into STRICT p_resultado
      from acad_carga_evaluacion ace
      where Ace.Id_Carga_Curso=p_id_carga_curso;

    end if;

    if (p_tipo='V') then
    
      select count(*) into STRICT p_resultado
      from acad_carga_evaluacion ace
      where ace.Fecha_Evaluacion<clock_timestamp()
      and not Exists (SELECT distinct 's' 
                      from Acad_Alumno_Evaluacion aae
                      where Aae.Id_Carga_Evaluacion=Ace.Id_Carga_Evaluacion)
      and Ace.Id_Carga_Curso=p_id_carga_curso;

    end if;

    if (p_tipo='E') then
    
      select count(*) into STRICT p_resultado
      from acad_carga_evaluacion ace
      where Exists (SELECT distinct 's' 
                      from Acad_Alumno_Evaluacion aae
                      where Aae.Id_Carga_Evaluacion=Ace.Id_Carga_Evaluacion)
      and Ace.Id_Carga_Curso=p_id_carga_curso;

    end if;

  return p_resultado;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
