CREATE OR REPLACE FUNCTION david.ft_verificacion (v_codigo text, v_tipo text) RETURNS varchar AS $body$
DECLARE


   p_res  varchar(200);


BEGIN



  if (v_tipo='1') then



    select 'S' into STRICT p_res

    from moises.verificacion where codigo_verificacion=v_codigo  LIMIT 1;



  end if;



  if (v_tipo='2') then



    select 'S'||estado into STRICT p_res

    from moises.verificacion where valor=v_codigo  LIMIT 1;



  end if;



  return p_res;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
