CREATE OR REPLACE FUNCTION david.is_date ( p_fecha timestamp(0) ) RETURNS bigint AS $body$
DECLARE

  l_num bigint;

BEGIN

  l_num := (case when to_char(p_fecha,'yyyy')>2100 then (to_char(p_fecha,'yyyy'))::numeric -1000::text
        else (case when to_char(p_fecha,'yyyy')>to_char(clock_timestamp(),'yyyy') then (to_char(p_fecha,'yyyy'))::numeric -100::text 
                    else  to_char(p_fecha,'yyyy') end) end)::numeric;
  /*select (to_date(to_char(p_fecha,'dd/mm')||'/'||
