        else (case when to_char(p_fecha,'yyyy')>to_char(sysdate,'yyyy') then to_char(to_number(to_char(p_fecha,'yyyy'))-100) 
                    else  to_char(p_fecha,'yyyy') end) end))) into l_num
    from dual;*/
  RETURN l_num;

EXCEPTION
  WHEN data_exception
  THEN
    RETURN 0;
END;
