CREATE OR REPLACE PROCEDURE david.pk_tr_validation_sp_has_photo_temp ( P_PERSON_ID bigint, P_PROCEDURE_TYPE_ID bigint, P_CURSOR OUT REFCURSOR) AS $body$
DECLARE

        P_STATUS  smallint;
        P_MESSAGE text;

BEGIN

        IF (P_PERSON_ID IS NOT NULL AND P_PERSON_ID::text <> '') AND (P_PROCEDURE_TYPE_ID IS NOT NULL AND P_PROCEDURE_TYPE_ID::text <> '') THEN

            SELECT (CASE
                        WHEN EXISTS (SELECT ID_ALUM_FOTO_TEMP
                                    FROM DAVID.TR_ALUM_FOTO_TEMP TAFT
                                    WHERE TAFT.ID_PERSONA = P_PERSON_ID
                                      AND TAFT.ID_TIPO_TRAMITE = P_PROCEDURE_TYPE_ID) THEN 1
                        ELSE 0 END)
            INTO STRICT P_STATUS
;

            IF P_STATUS = 0 THEN
                P_MESSAGE := 'AÃºn no hemos recibido su foto personal para diploma de grado de bachiller.';
            ELSE
                P_MESSAGE := 'foto personal para diploma de grado de bachiller adjuntado.';
            END IF;

            OPEN P_CURSOR FOR
                SELECT P_STATUS as STATUS, P_MESSAGE as MESSAGE
;

        ELSE
            OPEN P_CURSOR FOR
                SELECT 0 as STATUS, 'Plan de estudios requerido para validar.' as MESSAGE
;
        END IF;

    EXCEPTION
        WHEN OTHERS THEN
            OPEN P_CURSOR FOR
                SELECT 0 as STATUS, 'Se ha producido un error en la base de datos.' as MESSAGE
;


    END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
