CREATE OR REPLACE PROCEDURE david.ft_eliminar_contrato_48h (p_id_semestre integer, p_id_sede integer) AS $body$
DECLARE


  contrato CURSOR FOR
  SELECT aac.id_alumno_contrato
  from vw_acad_alumno_contrato aac inner join acad_semestre asm on asm.id_semestre=aac.id_semestre and asm.estado='1'
  where Aac.Id_Modo_Contrato=1 and aac.estado in ('0','M') and aac.id_sede=p_id_sede
  AND (coalesce(Aac.Fecha_Actualizacion,clock_timestamp())+2)<clock_timestamp()
  and aac.id_semestre=p_id_semestre 
;

 
BEGIN 
 
    for cc in contrato loop
      CALL pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
    end loop;

 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
