CREATE OR REPLACE PROCEDURE david.ft_eliminar_contrato_72h () AS $body$
DECLARE


  contrato CURSOR FOR
  SELECT aac.id_alumno_contrato
  from vw_acad_alumno_contrato aac inner join acad_semestre asm on asm.id_semestre=aac.id_semestre and asm.estado='1'
  where Aac.Id_Modo_Contrato<>4 and aac.estado in ('0','M')
  AND (coalesce(Aac.Fecha_Actualizacion,clock_timestamp())+3)<clock_timestamp()
  --and aac.id_semestre=107
 
;

 
BEGIN 
 
    for cc in contrato loop
      CALL pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
    end loop;

 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
