SET client_encoding TO 'UTF8';

SET search_path = david,public;
\set ON_ERROR_STOP ON





CREATE OR REPLACE PROCEDURE david.ft_eliminar_contrato_ci_tpp () AS $body$
DECLARE

    contrato CURSOR FOR
    SELECT vaac.id_alumno_contrato
    from vw_acad_alumno_contrato vaac inner join vw_acad_programa_estudio vape on vape.id_programa_estudio = vaac.id_programa_estudio
    where vaac.id_modo_contrato <> 4
    and vaac.estado in ('0','M')
    and vaac.id_sede = 3
    and vaac.id_tipo_contrato = 16
    and vape.id_facultad = 72
    --and vaac.id_programa_estudio in (412)
    and (coalesce(vaac.fecha_actualizacion,clock_timestamp())+2) < clock_timestamp()   --48h
    and vaac.id_semestre = 231;


BEGIN 

    for cc in contrato loop
      CALL pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
    end loop;

 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE david.ft_eliminar_contrato_ci_tpp () FROM PUBLIC;

