CREATE OR REPLACE PROCEDURE david.ft_eliminar_contrato_idiomas () AS $body$
DECLARE

  contrato CURSOR FOR
SELECT aac.id_alumno_contrato, AAC.SEMESTRE, AAC.ESTADO
  from DAVID.vw_acad_alumno_contrato aac
  inner join DAVID.acad_semestre asm on asm.id_semestre=aac.id_semestre 
  where Aac.Id_Modo_Contrato<>4 and aac.estado in ('0','M') and Aac.Id_Sede in (1,2,3) 
  and Aac.Id_Tipo_Contrato=16
  /*AND (nvl(Aac.Fecha_Actualizacion,sysdate)+2)<sysdate
  AND 
  ASM.ID_SEMESTRE
IN (
SELECT  ASI.ID_SEMESTRE   FROM DAVID.ACAD_SEMESTRE_IDIOMAS asi 
)*/
;

 
BEGIN 
    for cc in contrato LOOP
	    --dbms_output.put_line('TEST');
      CALL pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
      COMMIT;
    end loop;
 end;
$body$
LANGUAGE PLPGSQL
;
