CREATE OR REPLACE PROCEDURE david.ft_eliminar_contrato_ingles (p_id_semestre integer /*, p_id_sede in integer*/
) AS $body$
DECLARE


  contrato CURSOR FOR
  SELECT aac.id_alumno_contrato
  from vw_acad_alumno_contrato aac inner join vw_Acad_Programa_Estudio ape on Ape.Id_Programa_Estudio=Aac.Id_Programa_Estudio
  where Aac.Id_Modo_Contrato=1 and aac.estado in ('0','M') --and aac.id_sede=p_id_sede
  and Ape.Id_Facultad=72
  and aac.id_semestre=p_id_semestre 
  ORDER BY id_alumno_contrato
;

 
BEGIN 
 
    for cc in contrato loop
      CALL pcd_anular_contrato_alumno_n(cc.id_alumno_contrato);
      COMMIT;
    end loop;

 end;
$body$
LANGUAGE PLPGSQL
;
