CREATE OR REPLACE PROCEDURE david.ft_eliminar_contrato_proceso (p_id_semestre integer, p_id_programa_estudio integer) AS $body$
DECLARE


  contrato CURSOR FOR
  SELECT id_alumno_contrato
  from vw_acad_alumno_contrato where id_semestre=p_id_semestre and Id_Modo_Contrato=1 and Id_Programa_Estudio=p_id_programa_estudio and estado='0';

 
