CREATE OR REPLACE PROCEDURE david.ft_eliminar_variacion_1h () AS $body$
DECLARE


  contrato CURSOR FOR
  SELECT id_alumno_contrato
  from Vw_Acad_Alumno_Contrato where Id_Modo_Contrato=4
  and Id_Semestre=199 and estado<>'1' and Fecha_Registro<clock_timestamp()-(1/24) 
  order by id_alumno_contrato;

 
BEGIN 
 
    for cc in contrato loop
      CALL pcd_anular_variacion_alumno(cc.id_alumno_contrato);
    end loop;

 end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
